export class Lyric {
	constructor() {
		this.player = $ ('#player')
		this.currentTime = 0
		this.index = 0
		this.lyric = ''
		this.$audio = document.querySelector ('audio')
		this.intervalId = null
	}

	//歌词渲染
	template() {
		$ ('#player_all')
			.remove ()
		let html = `<div id="player_all" >
				        <div class="lyric_box">
				        </div>
			         </div>`
		this.player.append (html)
	}

	getLyric(songmid) {
		let _this = this
		const settings = {
			"url": `http://localhost:3300/lyric?songmid=${songmid}`,
			"method": "GET",
		};
		$.ajax (settings)
			.done (function (response) {
				let lyric = response.data.lyric
				lyric = lyric.match (/^\[\d{2}:\d{2}.\d{2}](.+)$/gm)
				_this.resetScroll (lyric)
				_this.lyric = lyric
				let html = lyric.map (item => {
					return `<div class="player-lyrics-line">${item.slice (10)}</div>`
				})
					.join ("")
				$ ('.lyric_box')
					.append (html)
				_this.startScroll ()
			});
	}

	//歌词滚动
	lyricTime(lyric) {
		return lyric.replace (/^\[(\d{2}):(\d{2}).*/, (match, p1, p2) => 60 * (+p1) + (+p2))
	}

	scrollLyric() {
		let _this = this
		_this.currentTime = Math.round (_this.$audio ? _this.$audio.currentTime : _this.currentTime + 1)
		if (_this.index === _this.lyric.length - 1) return
		for (let i = 0; i < _this.lyric.length; i++) {
			if (_this.lyricTime (_this.lyric[i]) <= _this.currentTime && (!_this.lyric[i + 1] || this.lyricTime (_this.lyric[i + 1]) >= _this.currentTime)) {
				let lt = $ ('.player-lyrics-line')
				$ (lt[_this.index])
					.removeClass ('inactive')
				$ (lt[i])
					.addClass ('inactive')
				_this.index = i
				break
			}
		}
		if (_this.index > 2) {
			let y = -(_this.index - 2) * 60
			$ ('.player-lyrics-line')
				.css ('transform', `translateY(${y}px)`)
		}
	}

	startScroll() {
		this.stopScroll ()
		this.intervalId = setInterval (
			this.scrollLyric.bind (this)
			, 1000)
	}

	stopScroll() {
		clearInterval (this.intervalId)
	}

	resetScroll() {
		this.stopScroll ()
		this.currentTime = 0
		this.index = 0
		$ ('.player-lyrics-line')
			.css ('transform', `translateY(0px)`)
		let $active = $ ('.inactive')
		if ($active) {
			$active.removeClass ('inactive')
		}
	}

	toScroll(currentTime) {
		this.currentTime = currentTime
	}

	restart() {
		this.resetScroll ()
		this.startScroll ()
	}
}

export class Prograss {
	constructor(duration) {
		this.nowTime = 0
		this.duration = duration || 0
		this.prograss = 0
		this.intervalId = null
		this.$progressRate = $ ('.music_progress')
		this.$playedTime = $ ('.played_time')
		this.$allTime = $ ('.duration')
		this.$audio = document.querySelector ('audio')
		this.$allTime.html (this.formatTime (this.duration))
	}

	start() {
		this.stop ()
		this.intervalId = setInterval (() => {
			this.update ()
		}, 10)
	}

	stop() {
		clearInterval (this.intervalId)
	}

	restart() {
		this.reset ()
		this.start ()
	}

	reset(duration) {
		this.stop ()
		this.nowTime = 0
		this.prograss = 0
		this.$progressRate.attr ('value', '0')
		this.$playedTime.html (this.formatTime (this.$audio.currentTime))
		if (duration) {
			this.duration = +duration
			this.$allTime.html (this.formatTime (this.duration))
		}
	}

	update() {
		this.prograss = this.$audio.currentTime / this.duration
		this.$progressRate.attr ('value', `${this.prograss * 100}`)
		this.$progressRate.css ('background-size', `${this.prograss * 100}% 100%`)
		this.$playedTime.html (this.formatTime (this.$audio.currentTime))
	}

	formatTime(time) {
		let min = Math.floor (time / 60)
		let sec = Math.floor (time % 60)
		if (min < 10) min = '0' + min
		if (sec < 10) sec = '0' + sec
		return `${min}:${sec}`
	}
}

export class Load {
	constructor(slot, replace = false) {
		this.slot = slot
		this.instead = replace
		this.dom = null
	}

	template(el) {
		let html = `
    <svg t="1603018131108" class="icon loading load_rotate"  viewBox="0 0 1024 1024" version="1.1" xmlns="http://www.w3.org/2000/svg"
         p-id="3289" >
        <path  d="M96 512c0-19.33 15.67-35 35-35s35 15.67 35 35c0 191.09 154.91 346 346 346s346-154.91 346-346-154.91-346-346-346c-19.33 0-35-15.67-35-35s15.67-35 35-35c229.75 0 416 186.25 416 416S741.75 928 512 928 96 741.75 96 512z"
              fill="#73ac00" p-id="3290"></path>
    </svg>
		`
		let dom = document.createElement ('div')
		dom.className = 'load_ico'
		$ (dom)
			.append (html)
		if (this.instead === true) {
			$ (el || this.slot).children ().hide ()
			$ (el || this.slot).append ($ (dom))
		} else {
			$ (el || this.slot)
				.append ($ (dom))
		}
		this.dom = $ (dom)

	}

	render() {
		this.dom.fadeIn ()
	}

	remove(el) {
		if (this.instead === true) {
			$ (el || this.slot)
				.children ()
				.fadeIn ()
		}
		this.dom.remove ()
	}
}

export class Tip{
	constructor() {
		this.color={
			'waring':'#E6A23C',
			'success':'#67C23A',
			'fail':'#F56C6C'
		}
	}
	template(message,colorType){
		let html = `
		<div class="tip" style="background-color:${this.color[colorType]} ">
            <span>${message}</span>
		</div>
		`
		$(html).appendTo($('body'))
		setTimeout(()=>{
			$('.tip').fadeOut()
		},1500)
	}
}

export class SetCookie{
	constructor() {
		this.tip = new Tip
			// GlobalFunc.prototype.tip()
		this.init()
	}

	set(cookie){
		let _this = this
		var settings = {
			"url": "http://localhost:3300/user/setCookie?=",
			"method": "POST",
			"timeout": 0,
			"headers": {
				"Content-Type": "application/json"
			},
			"data": JSON.stringify({"data":`"${cookie}"`}),
		};

		$.ajax(settings).done(function (response) {
			if(response.result ===200){
				_this.tip.template(`${response.data}`,'fail')
			}else if(response.result ===100){
				_this.tip.template(`${response.data}`,'success')
				setTimeout(()=>{
					window.location.reload();
				},400)
			}
			$('.cookie_form').fadeOut()
		})
	}

	init(){
		let form = $('.cookie_form')
		form.hide()
		let _this = this
		$('.set_cookie').click((e)=>{
			form.fadeToggle()
		})
		form.submit(function (e){
			e.preventDefault()
			let cookie = $('input[name=cookie]').val()
			_this.set(cookie)
		})
	}
	deleteCookie(){
		let _this = this
		var settings = {
			"url": "http://localhost:3300/user/setCookie?=",
			"method": "POST",
			"timeout": 0,
			"headers": {
				"Content-Type": "application/json"
			},
			"data": JSON.stringify({"data":""}),
		};

		$.ajax(settings).done(function (response) {
			_this.tip.template(`操作成功`,'waring')
			setTimeout(()=>{
				window.location.reload();
			},400)
		});
	}
}

